package ConditionalOperators;

public class Else {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int time = 20;
		
		if (time < 18) {
		  System.out.println("Good day.");
		} else {
		  System.out.println("Good evening.");
		}
		// Outputs "Good evening."
	}

}
